import React, { useState, useEffect } from "react";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";
import fetchDevices from "../components/Maas360Data";
import fetchMaaS360Data from "../components/Maas360Data";
import Modal from "react-modal";

const DeviceContainer = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: white;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceName = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;

const modalStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

Modal.setAppElement("#root");

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [modalIsOpen, setIsOpen] = useState(false);

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await fetchMaaS360Data();
        setDevices(data);
      } catch (error) {
        setError(error.message);
      }
    }
    fetchData();
  }, []);

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div>
      <h1
        css={css`
          color: cadetblue;
          font-size: 2rem;
          margin-bottom: 2rem;
        `}
      >
        Device List
      </h1>
      <div
        css={css`
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 40%;
          padding: 1rem;
          background-color: cadetblue;
          color: white;
          border-radius: 8px;
          cursor: pointer;
        `}
        onClick={openModal}
      >
        <div
          css={css`
            font-size: 3rem;
            margin-right: 1rem;
          `}
        >
          <FaMobileAlt />
        </div>
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={modalStyles}
        contentLabel="Device List Modal"
      >
        <h2>Device List</h2>
        <ul>
          {devices.map((device) => (
            <DeviceContainer key={device.ID}>
              <DeviceName>{device.device_name}</DeviceName>
              <p>ID: {device.ID}</p>
              <p>Model: {device.model}</p>
              <p>Status: {device.deviceStatus}</p>
              <p>Username: {device.username}</p>
              <p>Last Reported: {device.lastReported}</p>
              <p>OS: {device.os}</p>
            </DeviceContainer>
          ))}
        </ul>
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
}

export default DeviceList;
