import React, { useState, useEffect } from "react";
import axios from "axios";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";

function DeviceStatus() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/maas360/devices")
      .then((response) => {
        setDevices(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  const getDeviceStatus = (status) => {
    switch (status) {
      case "Active":
        return "active";
      case "Disabled":
        return "disabled";
      case "Inactive":
        return "inactive";
      default:
        return "";
    }
  };

  return (
    <div

      css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-top: 2rem;
        background-color: #f5f5f5;
        border: 5px solid #e0e0e0;
        max-width: 800px;
        margin-left: 2rem;
      `}
    >
      <h1
        css={css`
          font-size: 2.5rem;
          margin-bottom: 2rem;
          color: cadetblue;
        `}
      >
        Device Status
      </h1>
      <div
        css={css`
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 40%;
          padding: 1rem;
          background-color: cadetblue;
          color: white;
          border-radius: 8px;
          cursor: pointer;
        `}
        onClick={handleTotalDevicesClick}
      >
        <div
          css={css`
            font-size: 3.5rem;
            margin-right: 1rem;
          `}
        >
          <FaMobileAlt />
        </div>
        <div
          css={css`
            font-size: 2rem;
          `}
        >
          {devices.length}
        </div>
      </div>
      {showDevices && (
        <ul
          css={css`
            list-style-type: none;
            padding: 0;
            margin-top: 2rem;
            display: flex;
            flex-wrap: wrap;
            justify-content: center;
            gap: 2rem;
          `}
        >
          {devices.map((device) => (
            <li
              key={device.ID}
              css={css`
                display: flex;
                flex-direction: column;
                justify-content: space-between;
                align-items: center;
                width: 20rem;
                padding: 1rem;
                background-color: #f5f5f5;
                border: 5px solid #e0e0e0;
                border-radius: 8px;
              `}
            >
              <div
                css={css`
                  font-size: 3rem;
                  margin-bottom: 1rem;
                `}
              >
                <FaMobileAlt />
              </div>
              <h2
                css={css`
                  margin-bottom: 0.5rem;
                  color: cadetblue;
                  font-size: 1.5rem;
                  text-align: center;
                `}
              >
                {device.device_name}
              </h2>
              <div
                css={css`
                  display: flex;
                  flex-direction: column;
                  align-items: center;
                  gap: 0.5rem;
                  font-size: 1.2rem;
                  text-align: center;
                `}
              >
                <div>Status: {device.deviceStatus}</div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default DeviceStatus;
