import React, { useState, useEffect } from "react";
import axios from "axios";
import "./DeviceList.css";

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/maas360/devices")
      .then((response) => {
        setDevices(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  const handleShowDevices = () => {
    setShowDevices(!showDevices);
  };

  if (error) {
    return <div>Error: {error}</div>;
  } else {
    return (
      <div className="device-list-container">
        <div
          className="total-devices-box"
          onClick={handleShowDevices}
          style={{ cursor: "pointer" }}
        >
          <h1>Total Devices: {devices.length}</h1>
        </div>
        {showDevices && (
          <ul className="device-list">
            {devices.map((device) => (
              <li key={device.ID}>
                <h2>{device.device_name}</h2>
                <p>ID: {device.ID}</p>
                <p>Model: {device.model}</p>
                <p>Status: {device.deviceStatus}</p>
                <p>Username: {device.username}</p>
                <p>Last Reported: {device.lastReported}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    );
  }
}

export default DeviceList;
