import React, { useState, useEffect } from "react";
import styled from "@emotion/styled";
import { fetchTotalDevicesData } from './Maas360Data';

const TotalDevicesContainer = styled.div`
  margin: 1rem 0;
  padding: 1rem;
  background-color: ${(props) => props.theme.colors.primary};
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
  color: ${(props) => props.theme.colors.text};
`;

const TotalDevicesText = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
  color: ${(props) => props.theme.colors.text};
  text-align: center;
`;

function TotalDevices() {
  const [totalDevices, setTotalDevices] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await fetchTotalDevicesData();
        setTotalDevices(data);
        setIsLoading(false);
      } catch (error) {
        setError(error.message);
        setIsLoading(false);
      }
    }

    fetchData();
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (totalDevices === null) {
    return <div>No data to display</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <TotalDevicesContainer>
      <TotalDevicesText>Total Devices: {totalDevices}</TotalDevicesText>
    </TotalDevicesContainer>
  );
}

export default TotalDevices;
