import React, { useState, useEffect } from "react";
import axios from "axios";
import { css } from '@emotion/react';
import { FaMobileAlt } from 'react-icons/fa';
import "./DeviceList.css";

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:5000/maas360/devices")
      .then((response) => {
        setDevices(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  if (error) {
    return <div>Error: {error}</div>;
  } else {
    return (
      <div css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-top: 2rem;
      `}>
        <h1 css={css`
          color: cadetblue;
          font-size: 2rem;
          margin-bottom: 2rem;
        `}>Device List</h1>
        <div css={css`
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 40%;
          padding: 1rem;
          background-color: cadetblue;
          color: white;
          border-radius: 8px;
          cursor: pointer;
        `} onClick={handleTotalDevicesClick}>
          <div css={css`
            font-size: 2rem;
            margin-right: 1rem;
          `}><FaMobileAlt /></div>
          <div css={css`
            font-size: 2rem;
          `}>{devices.length}</div>
        </div>
        {showDevices && (
          <ul css={css`
            list-style-type: none;
            padding: 0;
            margin-top: 2rem;
          `}>
            {devices.map((device) => (
              <li key={device.ID} css={css`
                margin: 1rem 0;
                padding: 1rem;
                background-color: white;
                box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
                border-radius: 8px;
              `}>
                <h2 css={css`
                  margin-bottom: 0.5rem;
                `}>{device.device_name}</h2>
                <p css={css`
                  margin-bottom: 0.2rem;
                `}>ID: {device.ID}</p>
                <p css={css`
                  margin-bottom: 0.2rem;
                `}>Model: {device.model}</p>
                <p css={css`
                  margin-bottom: 0.2rem;
                `}>Status: {device.deviceStatus}</p>
                <p css={css`
                  margin-bottom: 0.2rem;
                `}>Username: {device.username}</p>
                <p>Last Reported: {device.lastReported}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    );
  }
}

export default DeviceList;
