import React, { useState, useEffect } from "react";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";
import MaaS360Data from "../components/Maas360Data";
import Modal from "react-modal";

const DeviceListContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 2rem;
  background-color: #f5f5f5;
  max-width: 800px;
`;

const DeviceListHeader = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 2rem;
`;

const IconContainer = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
`;

const DeviceCount = styled.div`
  margin-left: 1rem;
`;

const modalStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

Modal.setAppElement("#root");

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [modalIsOpen, setIsOpen] = useState(false);

  useEffect(() => {
    async function fetchDevices() {
      try {
        const data = await MaaS360Data();
        setDevices(data);
      } catch (error) {
        setError(error.message);
      }
    }
    fetchDevices();
  }, []);

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div>
      <DeviceListHeader>Device List</DeviceListHeader>
      <DeviceListContainer>
        <IconContainer onClick={openModal}>
          <FaMobileAlt />
          <DeviceCount>{devices.length}</DeviceCount>
        </IconContainer>
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          style={modalStyles}
          contentLabel="Device List Modal"
        >
          <h2>Devices</h2>
          <ul>
            {devices.map((device) => (
              <li key={device.ID}>
                <h3>{device.device_name}</h3>
                <p>ID: {device.ID}</p>
                <p>Model: {device.model}</p>
                <p>Status: {device.deviceStatus}</p>
                <p>Username: {device.username}</p>
                <p>Last Reported: {device.lastReported}</p>
                <p>OS: {device.os}</p>
              </li>
            ))}
          </ul>
          <button onClick={closeModal}>Close</button>
        </Modal>
      </DeviceListContainer>
    </div>
  );
}

export default DeviceList;
