import React, { useState, useEffect } from "react";
import axios from "axios";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/maas360/devices")
      .then((response) => {
        setDevices(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  return (
    <div
      className="device-list"
      css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-top: 2rem;
      `}
    >
      <h1
        css={css`
          color: cadetblue;
          font-size: 2rem;
          margin-bottom: 2rem;
        `}
      >
        Device List
      </h1>
      <div
        css={css`
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 40%;
          padding: 1rem;
          background-color: cadetblue;
          color: white;
          border-radius: 8px;
          cursor: pointer;
        `}
        onClick={handleTotalDevicesClick}
      >
        <div
          css={css`
            font-size: 3rem;
            margin-right: 1rem;
          `}
        >
          <FaMobileAlt />
        </div>
        <div
          css={css`
            font-size: 2rem;
          `}
        >
          {devices.length}
        </div>
      </div>
      {showDevices && (
        <ul
          css={css`
            list-style-type: none;
            padding: 0;
            margin-top: 2rem;
            display: flex;
            flex-wrap: wrap;
            justify-content: center;
            gap: 2rem;
          `}
        >
          {devices.map((device) => (
            <li
              key={device.ID}
              css={css`
                display: flex;
                flex-direction: column;
                justify-content: space-between;
                align-items: center;
                width: 20rem;
                padding: 1rem;
                background-color: white;
                box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
                border-radius: 8px;
              `}
            >
              <div
                css={css`
                  font-size: 3rem;
                  margin-bottom: 1rem;
                `}
              >
                <FaMobileAlt />
              </div>
              <h2
                css={css`
                  margin-bottom: 0.5rem;
                  color: cadetblue;
                  font-size: 1.5rem;
                  text-align: center;
                `}
              >
                {device.device_name}
              </h2>
              <div
                css={css`
                  display: flex;
                  flex-direction: column;
                  align-items: center;
                  gap: 0.5rem;
                  font-size: 1.2rem;
                  text-align: center;
                `}
              >
                <div>ID: {device.ID}</div>
                <div>Model: {device.model}</div>

                <div>Status: {device.deviceStatus}</div>

                <div>Username: {device.username}</div>
                <div>Last Reported: {device.lastReported}</div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default DeviceList;
