import React, { useState } from "react";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";
import MaaS360Data from "../components/MaaS360Data";

const DeviceContainer = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: white;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceName = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;

const StatusText = styled.div`
  font-size: 1.2rem;
  text-align: center;
`;

function DeviceStatus() {
  const [showDevices, setShowDevices] = useState(false);

  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  return (
    <div
      css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-top: 2rem;
        background-color: #f5f5f5;
        border: 5px solid #e0e0e0;
        max-width: 800px;
      `}
    >
      <h1
        css={css`
          color: cadetblue;
          font-size: 2rem;
          margin-bottom: 2rem;
        `}
      >
        Device Status
      </h1>
      <div
        css={css`
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 40%;
          padding: 1rem;
          background-color: cadetblue;
          color: white;
          border-radius: 8px;
          cursor: pointer;
        `}
        onClick={handleTotalDevicesClick}
      >
        <div
          css={css`
            font-size: 3rem;
            margin-right: 1rem;
          `}
        >
          <FaMobileAlt />
        </div>
        <MaaS360Data />
      </div>
      {showDevices && <MaaS360Data />}
    </div>
  );
}

export default DeviceStatus;
