import React, { useState, useEffect } from "react";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";
// import MaaS360Data from "../components/Maas360Data";
import { fetchMaaS360Data } from "../components/Maas360Data";
import Modal from "react-modal";

const DeviceContainer = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: cadetblue;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceName = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
  color: white;
  text-align: center;
`;

const StatusText = styled.div`
  font-size: 1.2rem;
  text-align: center;
  background-color: #42f5d4;
  border: 1px solid black;
  border-radius: 8px;
`;

const IconStyle = styled.div`
  font-size: 1.2rem;
  text-align: center;
  background-color: red;
  border: 2px solid black;
  border-radius: 8px;
  padding: 0.5rem;
  margin-bottom: 0.5rem;
  color: white;
  cursor: pointer;
  &:hover {
    background-color: #42f5d4;
  }
  &:active {
    background-color: #42f5d4;
  }
  &:focus {
    background-color: #42f5d4;
  }
  `;

const modalStyles = {
  content: {
    top: "55%",
    left: "50%",
    right: "auto",
    bottom: "5%",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    background: "gray",

  },
};

Modal.setAppElement("#root");

function DeviceStatus() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [modalIsOpen, setIsOpen] = useState(false);

  useEffect(() => {
    async function fetchDevices() {
      try {
        const data = await fetchMaaS360Data();
        setDevices(data);
      } catch (error) {
        setError(error.message);
      }
    }
    fetchDevices();
  }, []);

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  const totalDevices = devices.length;

  return (
    <StatusText>
      <h1>Device Status</h1>
      <h2>Total Devices: {totalDevices}</h2>
      <div onClick={openModal}>
        <IconStyle>
          <FaMobileAlt />
        </IconStyle>
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={modalStyles}
        contentLabel="Device Status Modal"
      >
        <h2>Device Status</h2>
        <ul>
          {devices.map((device) => (
            <DeviceContainer key={device.ID}>
              <DeviceName>{device.device_name}</DeviceName>
              <StatusText>Status: {device.deviceStatus}</StatusText>
              <StatusText>Username: {device.username}</StatusText>
            </DeviceContainer>
          ))}
        </ul>
        <button onClick={closeModal}>Close</button>
      </Modal>
    </StatusText>
  );
}

export default DeviceStatus;
