import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";

const DeviceListItemContainer = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: white;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceListItemIcon = styled.div`
  font-size: 3.5rem;
`;

const DeviceListItemDetails = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-left: 1rem;
`;

const DeviceListItemTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;

export async function fetchMaaS360Data() {
  try {
    const response = await axios.get("http://localhost:5000/api/maas360");
    const data = response.data;
    return data;
  } catch (error) {
    throw new Error("Failed to fetch data from the API");
  }
}

function MaaS360Data({ devices }) {
  return (
    <>
      {devices.map((device) => (
        <DeviceListItemContainer key={device.id}>
          <DeviceListItemIcon>
            <FaMobileAlt />
          </DeviceListItemIcon>
          <DeviceListItemDetails>
            <DeviceListItemTitle>{device.name}</DeviceListItemTitle>
            <p>ID: {device.id}</p>
            <p>OS: {device.os}</p>
          </DeviceListItemDetails>
        </DeviceListItemContainer>
      ))}
    </>
  );
}

export default MaaS360Data;
