import React from "react";
import { css } from "@emotion/react";
import { FaSearch } from "react-icons/fa";

const SearchBar = ({ searchTerm, handleInputChange, handleSearch }) => {
  return (
    <form
      onSubmit={handleSearch}
      css={css`
        display: flex;
        align-items: center;
        width: 100%;
        max-width: 800px;
        border: 1px solid #ccc;
        border-radius: 8px;
        overflow: hidden;
        margin-bottom: 2rem;
      `}
    >
      <div
        css={css`
          display: flex;
          align-items: center;
          width: 100%;
          padding: 1rem;
        `}
      >
        <input
          type="text"
          placeholder="Search for devices..."
          value={searchTerm}
          onChange={handleInputChange}
          css={css`
            flex: 1;
            padding: 1rem;
            font-size: 1.2rem;
            border: none;
          `}
        />
        <button
          type="submit"
          css={css`
            background-color: #3f51b5;
            color: white;
            font-size: 1.2rem;
            padding: 1rem;
            border: none;
            cursor: pointer;
            &:hover {
              background-color: #2c3e50;
            }
          `}
        >
          <FaSearch />
        </button>
      </div>
    </form>
  );
};

export default SearchBar;
