import React, { useState } from "react";
import styled from "@emotion/styled";
import { FaSearch } from "react-icons/fa";
import SearchBar from "../components/SearchPage/SearchBar";
import SearchFilter from "../components/SearchPage/SearchFilter";
import FilterCheckbox from "../components/SearchPage/FilterCheckbox";

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOption, setFilterOption] = useState("");

  const handleSearch = (event) => {
    event.preventDefault();
    // TODO: Implement search logic here
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilterOption(event.target.value);
  };

  const MainDiv = styled.div`
    margin: 1rem;
    padding-top: 5rem;
    display: flex;
    flex-direction: column;
    align-items: center;
  `;

  const SearchBarContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 2rem;
  `;

  const SearchBarDiv = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: center;
    width: 100%;
    max-width: 800px;
    margin-top: 1rem;
  `;

  const SearchResultsDiv = styled.div`
    display: flex;
    align-items: center;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    grid-gap: 1rem;
  `;

  const CheckboxDiv = styled.div`
  display: flex;
  flex-direction: row no-wrap;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  max-width: 800px;
  margin-right: 4rem;
`;


  return (
    <MainDiv>
      <h1>Device Database Search</h1>
      <SearchBarContainer>
        <SearchBarDiv>
          <SearchBar
            searchTerm={searchTerm}
            handleSearch={handleSearch}
            handleInputChange={handleInputChange}
          />
        </SearchBarDiv>
        <CheckboxDiv>
          <FilterCheckbox label="User Name" />
          <FilterCheckbox label="Quarantined Devices" />
          <FilterCheckbox label="OS Version" />
          <FilterCheckbox label="Archived" />
        </CheckboxDiv>
        <SearchFilter
          filterOption={filterOption}
          handleFilterChange={handleFilterChange}
        />
      </SearchBarContainer>

      <SearchResultsDiv>
        {searchTerm && <p>TODO: Render search results here</p>}
      </SearchResultsDiv>
    </MainDiv>
  );
};

export default SearchPage;
