import React, { useState } from "react";
import styled from "@emotion/styled";
import { FaSearch } from "react-icons/fa";
import SearchBar from "../components/SearchPage/SearchBar";
import SearchFilter from "../components/SearchPage/SearchFilter";

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOption, setFilterOption] = useState("");

  const handleSearch = (event) => {
    event.preventDefault();
    // TODO: Implement search logic here
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilterOption(event.target.value);
  };

  return (
    <MainDiv>
      <h1>Device Database Search</h1>
      <SearchBar
        searchTerm={searchTerm}
        handleSearch={handleSearch}
        handleInputChange={handleInputChange}
      />
      <SearchFilter filterOption={filterOption} handleFilterChange={handleFilterChange} />
      {/* TODO: Render search results here */}
      {searchTerm && (
        <SearchResultsDiv>
          {/* Render search results based on searchTerm and filterOption */}
        </SearchResultsDiv>
      )}
      {!searchTerm && <p>Enter a search term to get started!</p>}
    </MainDiv>
  );
};

const MainDiv = styled.div`
  margin: 1rem;
  padding-top: 5rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const SearchResultsDiv = styled.div`
  margin-top: 2rem;
  width: 100%;
  max-width: 800px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  grid-gap: 2rem;
`;

export default SearchPage;
