import React, { useState } from "react";
/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { FaSearch } from "react-icons/fa";

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOption, setFilterOption] = useState("");

  const handleSearch = (event) => {
    event.preventDefault();
    // TODO: Implement search logic here
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilterOption(event.target.value);
  };

  return (
    <div
      css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        padding: 2rem;
        margin-top: 4rem;
      `}
    >
      <h1
        css={css`
          font-size: 3rem;
          margin-bottom: 2rem;
        `}
      >
        Device Database Search
      </h1>
      <form
        onSubmit={handleSearch}
        css={css`
          display: flex;
          flex-direction: column;
          align-items: center;
          width: 100%;
          max-width: 800px;
          border: 1px solid #ccc;
          border-radius: 8px;
          overflow: hidden;
          margin-bottom: 2rem;
        `}
      >
        <div
          css={css`
            display: flex;
            align-items: center;
            width: 100%;
            padding: 1rem;
          `}
        >
          <input
            type="text"
            placeholder="Search for devices..."
            value={searchTerm}
            onChange={handleInputChange}
            css={css`
              flex: 1;
              padding: 1rem;
              font-size: 1.2rem;
              border: none;
            `}
          />
          <button
            type="submit"
            css={css`
              background-color: #3f51b5;
              color: white;
              font-size: 1.2rem;
              padding: 1rem;
              border: none;
              cursor: pointer;
              &:hover {
                background-color: #2c3e50;
              }
            `}
          >
            <FaSearch />
          </button>
        </div>
        <div
          css={css`
            display: flex;
            align-items: center;
            width: 100%;
            padding: 1rem;
          `}
        >
          <label
            htmlFor="filter-select"
            css={css`
              margin-right: 1rem;
            `}
          >
            Filter by:
          </label>
          <select
            name="filter-select"
            id="filter-select"
            value={filterOption}
            onChange={handleFilterChange}
            css={css`
              padding: 0.5rem;
              font-size: 1.2rem;
              border: 1px solid #ccc;
              border-radius: 8px;
            `}
          >
            <option value="">All</option>
            <option value="55th Wiing">55th Wiing</option>
            <option value="38th Reconnaissance Squadron">
              38th Reconnaissance Squadron
            </option>
            <option value="45th Reconnaissance Squadron">
              45th Reconnaissance Squadron
            </option>
            <option value="55th Intelligence Support Squadron">
              55th Intelligence Support Squadron
            </option>
            <option value="55th Operations Support Squadron">
              55th Operations Support Squadron
            </option>
            <option value="82nd Reconnaissance Squadron">
              82nd Reconnaissance Squadron
            </option>
            <option value="95th Reconnaissance Squadron">
              95th Reconnaissance Squadron
            </option>
            <option value="97th Intelligence Squadron">
              97th Intelligence Squadron
            </option>
            <option value="390th Intelligence Squadron">
              390th Intelligence Squadron
            </option>
            <option value="488th Intelligence Squadron">
              488th Intelligence Squadron
            </option>
            <option value="338th Combat Training Squadron">
              338th Combat Training Squadron
            </option>
            <option value="343rd Reconnaissance Squadron">
              343rd Reconnaissance Squadron
            </option>
          </select>
          {/* TODO: Render search results here */}
          {searchTerm && (
            <div
              css={css`
                margin-top: 2rem;
                width: 100%;
                max-width: 800px;
                display: grid;
                grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
                grid-gap: 2rem;
              `}
            >
              {/* Render search results based on searchTerm and filterOption */}
            </div>
          )}
          {!searchTerm && (
            <p
              css={css`
                margin-top: 2rem;
                font-size: 1.2rem;
              `}
            >
              Enter a search term to get started!
            </p>
          )}
        </div>
      </form>
    </div>
  );
};

export default SearchPage;
