import React, { useState, useEffect } from "react";
import axios from "axios";
/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/maas360/devices")
      .then((response) => {
        setDevices(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  return (
    <div
      css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-top: 2rem;
        background-color: #f5f5f5;
        border: 5px solid #e0e0e0;
        max-width: 800px;
      `}
    >
      <h1
        css={css`
          font-size: 2.5rem;
          margin-bottom: 2rem;
        `}
      >
        Device List
      </h1>
      <div
        css={css`
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 40%;
          padding: 1rem;
          background-color: #f5f5f5;
          border-radius: 8px;
        `}
        onClick={handleTotalDevicesClick}
      >
        <div
          css={css`
            font-size: 3.5rem;
          `}
        >
          <FaMobileAlt />
        </div>
        <div
          css={css`
            font-size: 2.5rem;
          `}
        >
          {devices.length}
        </div>
      </div>
      {showDevices && (
        <ul
          css={css`
            display: flex;
            flex-direction: column;
            list-style-type: none;
            padding: 0;
            margin-top: 2rem;
          `}
        >
          {devices.map((device) => (
            <li
              key={device.ID}
              css={css`
                margin: 1rem 0;
                padding: 1rem;
                background-color: white;
                box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
                border-radius: 8px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                width: 80%;
              `}
            >
              <div
                css={css`
                  font-size: 3.5rem;
                `}
              >
                <FaMobileAlt />
              </div>
              <div
                css={css`
                  display: flex;
                  flex-direction: column;
                  align-items: flex-start;
                  margin-left: 1rem;
                `}
              >
                <h2
                  css={css`
                    font-size: 1.5rem;
                    margin-bottom: 0.5rem;
                  `}
                >
                  {device.device_name}
                </h2>
                <p>ID: {device.ID}</p>
                <p>Model: {device.model}</p>
                <p>Status: {device.deviceStatus}</p>
                <p>Username: {device.username}</p>
                <p>Last Reported: {device.lastReported}</p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default DeviceList;
