import React, { useState } from "react";
import styled from "@emotion/styled";
import SearchBar from "../components/SearchPage/SearchBar";
import SearchFilter from "../components/SearchPage/SearchFilter";
import FilterCheckbox from "../components/SearchPage/FilterCheckbox";

const MainDiv = styled.div`
  margin: 1rem;
  padding-top: 5rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const SearchRow = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 1rem;
  margin-bottom: 2rem;
`;

const SearchResultsDiv = styled.div`
  width: 100%;
  max-width: 800px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  grid-gap: 2rem;
`;

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOption, setFilterOption] = useState("");

  const handleSearch = (event) => {
    event.preventDefault();
    // TODO: Implement search logic here
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilterOption(event.target.value);
  };

  return (
    <MainDiv>
      <h1>Device Database Search</h1>
      <SearchRow>
        <SearchBar
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
          handleSearch={handleSearch}
        />
        <div>
          <FilterCheckbox label="User Name" />
          <FilterCheckbox label="Quarantined Devices" />
          <FilterCheckbox label="OS Version" />
          <FilterCheckbox label="Archived" />
        </div>
        <SearchFilter
          filterOption={filterOption}
          handleFilterChange={handleFilterChange}
        />
      </SearchRow>
      {/* Render search results here */}
      {searchTerm && (
        <SearchResultsDiv>{/* Render search results based on searchTerm and filterOption */}</SearchResultsDiv>
      )}
      {!searchTerm && <p>Enter a search term to get started!</p>}
    </MainDiv>
  );
};

export default SearchPage;
