import React, { useState, useEffect } from "react";
import styled from "@emotion/styled";
import { fetchTotalDevicesData } from './Maas360Data';

const TotalDevicesContainer = styled.div`
  margin: 1rem 0;
  padding: 1rem;
  background-color: ${(props) => props.theme.colors.primary};
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const TotalDevicesText = styled.h1`
font-size: 1.5rem;
margin-bottom: 0.5rem;
color: ${(props) => props.theme.colors.text};
text-align: center;
`;


const StatusBox = styled.div`
  font-size: 1.2rem;
  text-align: center;
  align-items: center;
  justify-content: center;
  margin: 0.5rem;
  padding: 0.5rem;
  border-radius: 8px;
  background-color: ${(props) => props.theme.colors.primary};
  color: ${(props) => props.theme.colors.text};
`;

function TotalDevices() {
  const [totalDevices, setTotalDevices] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await fetchTotalDevicesData();
        setTotalDevices(data);
        setIsLoading(false);
      } catch (error) {
        setError(error.message);
        setIsLoading(false);
      }
    }

    fetchData();
  }, []);

  if (isLoading) {
    return (
      <StatusBox>
        <div>Loading...</div>
      </StatusBox>
    );
  }

  if (totalDevices === null) {
    return (
      <StatusBox>
        <div>No data to display</div>
      </StatusBox>
    );
  }

  if (error) {
    return (
      <StatusBox>
        <div>Error: {error}</div>
      </StatusBox>
    );
  }

  return (
    <StatusBox>
      <TotalDevicesContainer>
        <TotalDevicesText>Total Devices</TotalDevicesText>
            <h2>{totalDevices}</h2>
      </TotalDevicesContainer>
    </StatusBox>
  );
}

export default TotalDevices;
