import React, { useState } from "react";
import styled from "@emotion/styled";
import DeviceList from "../components/DeviceList";
import DeviceStatus from "../components/DeviceStatus";
import { FaMobileAlt } from "react-icons/fa";
import Modal from "../components/DashboardComponents/Modal";
import ModalContent from "../components/ModalContent";

const ParentContainer = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: flex-start;
  flex-wrap: wrap;
  margin: 1rem;
  border: 3px solid ${props => props.theme.primaryColor};
  border-radius: 10px;
  max-width: 70vw;
  max-height: 70vh;
`;

const ChildContainer = styled.div`
  display: flex;
  margin: 1rem;
  border: 5px solid black;
  border-radius: 10px;
  background-color: ${props => props.theme.secondaryColor};
  align-items: center;
`;

const MobileIcon = styled(FaMobileAlt)`
  font-size: 3rem;
  color: ${props => props.theme.primaryColor};
  cursor: pointer;
`;

const MainDiv = styled.div`
  margin: 1rem;
  padding-top: 5rem;
`;

const PageStyle = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  border: 5px solid gray;
  border-radius: 10px;
  background-color: ${props => props.theme.primaryColor};
  height: 100%;
`;

const Filter = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  margin: 1rem;
  border: 3px solid ${props => props.theme.primaryColor};
  border-radius: 10px;
  max-width: 70vw;
  max-height: 70vh;
`;

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <MainDiv>
      <Filter>
        {/* DashboardFilter component goes here */}
      </Filter>
      <PageStyle>
        <ParentContainer>
          <ChildContainer>
            <MobileIcon onClick={toggleModal} />
            <h2>Total Devices: 100</h2>
          </ChildContainer>
          <ChildContainer>
            <div>Component Placeholder 2</div>
          </ChildContainer>
          <ChildContainer>
            <div>Component Placeholder 3</div>
          </ChildContainer>
          <ChildContainer>
            <div>Component Placeholder 4</div>
          </ChildContainer>
          <ChildContainer>
            <div>Component Placeholder 5</div>
          </ChildContainer>
        </ParentContainer>
        <ParentContainer>
          <ChildContainer>
            <div>Component Placeholder 6</div>
          </ChildContainer>
          <ChildContainer>
            <div>Component Placeholder 7</div>
          </ChildContainer>
          <ChildContainer>
            <div>Component Placeholder 8</div>
          </ChildContainer>
          <ChildContainer>
            <div>Component Placeholder 9</div>
          </ChildContainer>
          <ChildContainer>
            <div>Component Placeholder 10</div>
          </ChildContainer>
        </ParentContainer>
      </PageStyle>
      {isModalOpen && (
        <Modal onClose={toggleModal}>
          <ModalContent />
        </Modal>
      )}
    </MainDiv>
  );
};

export default Dashboard;
