import React, { useState, useEffect } from "react";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";
import MaaS360Data from "../components/Maas360Data";
import Modal from 'react-modal';

const DeviceListContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 2rem;
  background-color: #f5f5f5;
  max-width: 800px;
`;

const DeviceListHeader = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 2rem;
`;

const DeviceListItemContainer = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: white;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceListItemIcon = styled.div`
  font-size: 3.5rem;
`;

const DeviceListItemDetails = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-left: 1rem;
`;

const DeviceListItemTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;

const DeviceDetailsContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const DeviceDetailsButton = styled.button`
  background-color: #e0e0e0;
  border: none;
  border-radius: 4px;
  color: #333;
  cursor: pointer;
  font-size: 1rem;
  margin-top: 1rem;
  padding: 0.5rem 1rem;

  &:hover {
    background-color: #ccc;
  }
`;

const DeviceDetailsHeader = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 2rem;
`;

const DeviceDetailsList = styled.ul`
  list-style: none;
  padding: 0;
`;

const DeviceDetailsListItem = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: white;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceDetailsListItemIcon = styled.div`
  font-size: 3.5rem;
`;

const DeviceDetailsListItemText = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-left: 1rem;
`;

const DeviceDetailsListItemTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;


Modal.setAppElement('#root');

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    async function fetchDevices() {
      try {
        const data = await MaaS360Data();
        setDevices(data);
        setIsLoading(false);
      } catch (error) {
        setError(error.message);
        setIsLoading(false);
      }
    }
    fetchDevices();
  }, []);


  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  const handleDeviceClick = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div>
      <DeviceListHeader>Device List</DeviceListHeader>
      <DeviceListContainer>
        <div onClick={handleTotalDevicesClick}>
          <div>
            <FaMobileAlt />
          </div>
          <div>{devices.length}</div>
        </div>
        {showDevices && (
          <ul>
            {devices.map((device) => (
              <DeviceListItemContainer key={device.ID} onClick={handleDeviceClick}>
                <DeviceListItemIcon>
                  <FaMobileAlt />
                </DeviceListItemIcon>
                <DeviceListItemDetails>
                  <DeviceListItemTitle>{device.device_name}</DeviceListItemTitle>
                  <p>ID: {device.ID}</p>
                  <p>Model: {device.model}</p>
                  <p>Status: {device.deviceStatus}</p>
                  <p>Username: {device.username}</p>
                  <p>Last Reported: {device.lastReported}</p>
                  <p>OS: {device.os}</p>
                </DeviceListItemDetails>
              </DeviceListItemContainer>
            ))}
          </ul>
        )}
        <Modal
  isOpen={isModalOpen}
  onRequestClose={closeModal}
  contentLabel="Device Details"
>
  <DeviceDetailsContainer>
    <DeviceDetailsHeader>Device Details</DeviceDetailsHeader>
    <DeviceDetailsList>
      {devices.map((device) => (
        <DeviceDetailsListItem key={device.ID}>
          <DeviceDetailsListItemIcon>
            <FaMobileAlt />
          </DeviceDetailsListItemIcon>
          <DeviceDetailsListItemText>
            <DeviceDetailsListItemTitle>{device.device_name}</DeviceDetailsListItemTitle>
            <p>ID: {device.ID}</p>
            <p>Model: {device.model}</p>
            <p>Status: {device.deviceStatus}</p>
            <p>Username: {device.username}</p>
            <p>Last Reported: {device.lastReported}</p>
            <p>OS: {device.os}</p>
          </DeviceDetailsListItemText>
        </DeviceDetailsListItem>
      ))}
    </DeviceDetailsList>
    <DeviceDetailsButton onClick={closeModal}>Close</DeviceDetailsButton>
  </DeviceDetailsContainer>
</Modal>

      </DeviceListContainer>
    </div>
  );
}

export default DeviceList;
