import React, { useState, useEffect } from "react";
import axios from "axios";
/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/maas360/devices")
      .then((response) => {
        setDevices(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  return (
    <div
      className="device_list"
      css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-top: 2rem;

        h1 {
          color: cadetblue;
          font-size: 2rem;
          margin-bottom: 2rem;
        }

        div:nth-of-type(2) {
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 40%;
          padding: 1rem;
          background-color: cadetblue;
          color: white;
          border-radius: 8px;
          cursor: pointer;

          div:first-of-type {
            font-size: 2rem;
            margin-right: 1rem;
          }

          div:last-of-type {
            font-size: 2rem;
          }
        }

        ul {
          list-style-type: none;
          padding: 0;
          margin-top: 2rem;

          li {
            margin: 5rem 0;
            padding: 1rem;
            background-color: cadetblue;
            box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
            border-radius: 8px;

            h2 {
              margin-bottom: 0.5rem;
              color: cadetblue;
            }

            p {
              margin-bottom: 0.2rem;
            }

            p:nth-of-type(1) {
              color: black;
            }
          }
        }
      `}
    >
      <h1>Device List</h1>
      <div onClick={handleTotalDevicesClick}>
        <div>
          <FaMobileAlt />
        </div>
        <div>{devices.length}</div>
      </div>
      {showDevices && (
        <ul>
          {devices.map((device) => (
            <li key={device.ID}>
              <h2>{device.device_name}</h2>
              <p>ID: {device.ID}</p>
              <p>Model: {device.model}</p>
              <p>Status: {device.deviceStatus}</p>
              <p>Username: {device.username}</p>
              <p>Last Reported: {device.lastReported}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default DeviceList;
