import React, { useState, useEffect } from "react";
import axios from "axios";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";
import MaaS360Data from "./MaaS360Data";

const DeviceListContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 2rem;
  background-color: #f5f5f5;
  max-width: 800px;
`;

const DeviceListHeader = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 2rem;
`;

const DeviceListItemContainer = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: white;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceListItemIcon = styled.div`
  font-size: 3.5rem;
`;

const DeviceListItemDetails = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-left: 1rem;
`;

const DeviceListItemTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;

function DeviceList() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/maas360/devices")
      .then((response) => {
        setDevices(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  return (
    <div>
      <DeviceListHeader>Device List</DeviceListHeader>
      <DeviceListContainer>
        <div onClick={handleTotalDevicesClick}>
          <div>
            <FaMobileAlt />
          </div>
          <div>{devices.length}</div>
        </div>
        {showDevices && (
          <ul>
            {devices.map((device) => (
              <DeviceListItemContainer key={device.ID}>
                <DeviceListItemIcon>
                  <FaMobileAlt />
                </DeviceListItemIcon>
                <DeviceListItemDetails>
                  <DeviceListItemTitle>{device.device_name}</DeviceListItemTitle>
                  <MaaS360Data device={device} />
                </DeviceListItemDetails>
              </DeviceListItemContainer>
            ))}
          </ul>
        )}
      </DeviceListContainer>
    </div>
  );
}

export default DeviceList;
