import React, { useState } from "react";
import styled from "@emotion/styled";
import SearchBar from "../components/SearchPage/SearchBar";
import SearchFilter from "../components/SearchPage/SearchFilter";
import FilterCheckbox from "../components/SearchPage/FilterCheckbox";

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOption, setFilterOption] = useState("");

  const handleSearch = (event) => {
    event.preventDefault();
    // TODO: Implement search logic here
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value.slice(0, 30));
  };

  const handleFilterChange = (event) => {
    setFilterOption(event.target.value);
  };

  const MainDiv = styled.div`
    margin: 1rem;
    padding-top: 5rem;
    display: flex;
    flex-direction: column;
    align-items: center;
  `;

  const SearchBarDiv = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-bottom: 1rem;
  `;

  const SearchResultsDiv = styled.div`
    width: 100%;
    max-width: 1000px;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    grid-gap: 1rem;
  `;

  return (
    <MainDiv>
      <h1>Device Database Search</h1>
      <SearchBarDiv>
        <SearchBar
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
          handleSearch={handleSearch}
          handleInputChange={handleInputChange}
        />
        <FilterCheckbox label="User Name" />
        <FilterCheckbox label="Quarantined Devices" />
        <FilterCheckbox label="OS Version" />
        <FilterCheckbox label="Archived" />
      </SearchBarDiv>
      <SearchFilter
        filterOption={filterOption}
        handleFilterChange={handleFilterChange}
      />
      {searchTerm && (
        <SearchResultsDiv>{/* Render search results here */}</SearchResultsDiv>
      )}
      {!searchTerm && <p>Enter a search term to get started!</p>}
    </MainDiv>
  );
};

export default SearchPage;
