import React, { useState } from "react";
import styled from "@emotion/styled";
import { FaSearch } from "react-icons/fa";
import SearchBar from "../components/SearchPage/SearchBar";
import SearchFilter from "../components/SearchPage/SearchFilter";
import FilterCheckbox from "../components/SearchPage/FilterCheckbox";

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOptions, setFilterOptions] = useState({
    userName: false,
    quarantinedDevices: false,
    osVersion: false,
    archived: false,
  });

  const handleSearch = (event) => {
    event.preventDefault();
    // TODO: Implement search logic here
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value.slice(0, 30));
  };

  const handleFilterChange = (event) => {
    const { name, checked } = event.target;
    setFilterOptions((prevState) => ({ ...prevState, [name]: checked }));
  };

  const MainDiv = styled.div`
    margin: 1rem;
    padding-top: 5rem;
    display: flex;
    flex-direction: column;
    align-items: center;
  `;

  const SearchBarDiv = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1rem;
  `;

  const FilterBarDiv = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
  `;

  const SearchResultsDiv = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    grid-gap: 1rem;
    margin-top: 1rem;
  `;

  return (
    <MainDiv>
      <h1>Device Database Search</h1>
      <SearchBarDiv>
        <SearchBar
          searchTerm={searchTerm}
          handleSearch={handleSearch}
          handleInputChange={handleInputChange}
        />
      </SearchBarDiv>
      <FilterBarDiv>
        <FilterCheckbox
          label="User Name"
          name="userName"
          checked={filterOptions.userName}
          onChange={handleFilterChange}
        />
        <FilterCheckbox
          label="Quarantined Devices"
          name="quarantinedDevices"
          checked={filterOptions.quarantinedDevices}
          onChange={handleFilterChange}
        />
        <FilterCheckbox
          label="OS Version"
          name="osVersion"
          checked={filterOptions.osVersion}
          onChange={handleFilterChange}
        />
        <FilterCheckbox
          label="Archived"
          name="archived"
          checked={filterOptions.archived}
          onChange={handleFilterChange}
        />
      </FilterBarDiv>
      <SearchResultsDiv>
        {searchTerm && <p>TODO: Render search results here</p>}
      </SearchResultsDiv>
      {!searchTerm && <p>Enter a search term to get started!</p>}
    </MainDiv>
  );
};

export default SearchPage;
