import React, { useState } from "react";
import styled from "@emotion/styled";
import { FaSearch } from "react-icons/fa";
import SearchBar from "../components/SearchPage/SearchBar";
import SearchFilter from "../components/SearchPage/SearchFilter";
import FilterCheckbox from "../components/SearchPage/FilterCheckbox";

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOption, setFilterOption] = useState("");

  const handleSearch = (event) => {
    event.preventDefault();
    // TODO: Implement search logic here
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilterOption(event.target.value);
  };

  const MainDiv = styled.div`
    margin: 1rem;
    padding-top: 5rem;
    display: flex;
    flex-direction: column;
    align-items: center;
  `;

  const SubDiv = styled.div`
    margin: 1rem;
    padding-top: 1rem;
    display: flex;
    flex-direction: column;
    align-items: center;
  `;

  const SearchBarDiv = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
  `;

  const FilterDiv = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    margin-top: 1.5rem;

    label {
      display: flex;
      align-items: center;
      margin-bottom: 0.5rem;
      cursor: pointer;

      input[type="checkbox"] {
        margin-right: 0.5rem;
        width: 1.5rem;
        height: 1.5rem;
        cursor: pointer;
      }
    }
  `;

  const SearchResultsDiv = styled.div`
    width: 100%;
    max-width: 1000px;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    grid-gap: 1rem;
  `;

  return (
    <MainDiv>
      <h1>Device Database Search</h1>
      <SearchBarDiv>
        <SearchBar
          searchTerm={searchTerm}
          handleSearch={handleSearch}
          handleInputChange={handleInputChange}
        />
      </SearchBarDiv>
      <SubDiv>
        <SearchFilter
          filterOption={filterOption}
          handleFilterChange={handleFilterChange}
        />
        <FilterDiv>
          <label>
            <input type="checkbox" name="user-name" />
            User Name
          </label>
          <label>
            <input type="checkbox" name="quarantined-devices" />
            Quarantined Devices
          </label>
          <label>
            <input type="checkbox" name="os-version" />
            OS Version
          </label>
          <label>
            <input type="checkbox" name="archived" />
            Archived
          </label>
        </FilterDiv>
      </SubDiv>
      TODO: Render search results here
      {searchTerm && <SearchResultsDiv></SearchResultsDiv>}
      {!searchTerm && <p>Enter a search term to get started!</p>}
    </MainDiv>
  );
};

export default SearchPage;
