import React from "react";
import { Link } from "react-router-dom";
/** @jsxImportSource @emotion/react */
import { css, ThemeProvider } from "@emotion/react";

const HomePage = () => {
  return (
    <div
      className="home"
      css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        height: calc(100vh - 4rem); /* subtract the height of the navbar */
        width: 100vw;
        background-color: ${(props) => props.theme.primaryColor};
        position: absolute;
        top: 4rem; /* set the top position to the height of the navbar */
        left: 0;
        color: ${(props) => props.theme.textColor};
      `}
    >
      <h1
        css={css`
          font-size: 3rem;
          margin-bottom: 2rem;
          color: ${(props) => props.theme.headingColor};
        `}
      >
        Bockscar Device Tracker
      </h1>
      <p
        css={css`
          font-size: 1.5rem;
          text-align: center;
          max-width: 600px;
        `}
      >
        Welcome to the Bockscar Device Tracker. This application allows you to
        track and manage mobile devices within your military unit. Get real-time
        information, search device history, and more.
      </p>
    </div>
  );
};

export default HomePage;
