import React, { useState, useEffect } from "react";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";
import MaaS360Data from "../components/Maas360Data";
import Modal from 'react-modal';
import e from "cors";

const DeviceListContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 2rem;
  background-color: #f5f5f5;
  max-width: 800px;
`;

const DeviceListHeader = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 2rem;
`;

const DeviceListItemContainer = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: white;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceListItemIcon = styled.div`
  font-size: 3.5rem;
`;

const DeviceListItemDetails = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-left: 1rem;
`;

const DeviceListItemTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;

function DeviceList({ devices }) {
  const [selectedDevice, setSelectedDevice] = useState(null);

  const handleDeviceClick = (device) => {
    setSelectedDevice(device);
  };

  const handleCloseModal = () => {
    setSelectedDevice(null);
  };

  return (
    <>
      <h1>Device List</h1>
      <ul>
        {devices.map((device) => (
          <li key={device.ID} onClick={() => handleDeviceClick(device)}>
            {device.device_name}
          </li>
        ))}
      </ul>
      <Modal isOpen={!!selectedDevice} onRequestClose={handleCloseModal}>
        {selectedDevice && (
          <>
            <h2>{selectedDevice.device_name}</h2>
            <p>ID: {selectedDevice.ID}</p>
            <p>Model: {selectedDevice.model}</p>
            <p>Status: {selectedDevice.deviceStatus}</p>
            <p>Username: {selectedDevice.username}</p>
            <p>Last Reported: {selectedDevice.lastReported}</p>
            <p>OS: {selectedDevice.os}</p>
            <button onClick={handleCloseModal}>Close Modal</button>
          </>
        )}
      </Modal>
    </>
  );
}

export default DeviceList;
