import React, { useState, useEffect } from "react";
import axios from "axios";
import { css } from "@emotion/react";
import { FaMobileAlt } from "react-icons/fa";
import styled from "@emotion/styled";

const DeviceContainer = styled.li`
  margin: 1rem 0;
  padding: 1rem;
  background-color: white;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
`;

const DeviceName = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;

const StatusText = styled.div`
  font-size: 1.2rem;
  text-align: center;
`;

function DeviceStatus() {
  const [devices, setDevices] = useState([]);
  const [error, setError] = useState(null);
  const [showDevices, setShowDevices] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:5000/maas360/devices")
      .then((response) => {
        setDevices(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });
  }, []);

  const handleTotalDevicesClick = () => {
    setShowDevices(!showDevices);
  };

  const getDeviceStatus = (status) => {
    switch (status) {
      case "Active":
        return "active";
      case "Disabled":
        return "disabled";
      case "Inactive":
        return "inactive";
      default:
        return "";
    }
  };

  return (
    <div
      css={css`
        display: flex;
        flex-direction: column;
        align-items: center;
        margin-top: 2rem;
        background-color: #f5f5f5;
        border: 5px solid #e0e0e0;
        max-width: 800px;
      `}
    >
      <h1
        css={css`
          color: cadetblue;
          font-size: 2rem;
          margin-bottom: 2rem;
        `}
      >
        Device Status
      </h1>
      <div
        css={css`
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 40%;
          padding: 1rem;
          background-color: cadetblue;
          color: white;
          border-radius: 8px;
          cursor: pointer;
        `}
        onClick={handleTotalDevicesClick}
      >
        <div
          css={css`
            font-size: 3rem;
            margin-right: 1rem;
          `}
        >
          <FaMobileAlt />
        </div>
        <div
          css={css`
            font-size: 2rem;
          `}
        >
          {devices.length}
        </div>
      </div>
      {showDevices && (
        <ul
          css={css`
            list-style-type: none;
            padding: 0;
            margin-top: 2rem;
            display: flex;
            flex-wrap: wrap;
            justify-content: center;
            gap: 2rem;
          `}
        >
          {devices.map((device) => (
            <DeviceContainer key={device.ID}>
              <div
                css={css`
                  font-size: 3rem;
                  margin-bottom: 1rem;
                `}
              >
                <FaMobileAlt />
              </div>
              <div
                css={css`
                  display: flex;
                  flex-direction: column;
                  align-items: flex-start;
                  margin-left: 1rem;
                `}
              >
                <DeviceName>{device.Name}</DeviceName>
                <StatusText
                  css={css`
                    color: ${getDeviceStatus(device.Status)};
                  `}
                >
                  {device.Status}
                </StatusText>
              </div>
            </DeviceContainer>
          ))}
        </ul>
      )}
    </div>
  );
}
export default DeviceStatus;
